import { useCallback, useEffect, useMemo, useRef } from "react";
import type { ReactElement } from "react";
import type { GroupBase, MenuListProps } from "react-select";
import type { ShouldLoadMore } from "./types";

export const CHECK_TIMEOUT = 300;

export type BaseSelectProps = {
	handleScrolledToBottom?: () => void;
	shouldLoadMore: ShouldLoadMore;
};

type MenuListType<
	Option = unknown,
	IsMulti extends boolean = boolean,
	Group extends GroupBase<Option> = GroupBase<Option>,
> = (props: MenuListProps<Option, IsMulti, Group>) => ReactElement;

export function wrapMenuList<
	Option = unknown,
	IsMulti extends boolean = boolean,
	Group extends GroupBase<Option> = GroupBase<Option>,
>(MenuList: MenuListType<Option, IsMulti, Group>) {
	function WrappedMenuList(props: MenuListProps<Option, IsMulti, Group>) {
		const { selectProps, innerRef } = props;

		const { handleScrolledToBottom, shouldLoadMore } =
			selectProps as unknown as BaseSelectProps;

		const checkTimeoutRef = useRef<NodeJS.Timeout>();
		const menuListRef = useRef<HTMLElement>(null);

		const shouldHandle = useCallback(() => {
			const el = menuListRef.current;

			// menu is not rendered
			if (!el) {
				return false;
			}

			const { scrollTop, scrollHeight, clientHeight } = el;

			return shouldLoadMore(scrollHeight, clientHeight, scrollTop);
		}, [shouldLoadMore]);

		const checkAndHandle = useCallback(() => {
			if (shouldHandle()) {
				if (handleScrolledToBottom) {
					handleScrolledToBottom();
				}
			}
		}, [shouldHandle, handleScrolledToBottom]);

		const setCheckAndHandleTimeout = useMemo(() => {
			const res = () => {
				checkAndHandle();

				checkTimeoutRef.current = setTimeout(res, CHECK_TIMEOUT);
			};

			return res;
		}, [checkAndHandle]);

		// biome-ignore lint/correctness/useExhaustiveDependencies: call only on init
		useEffect(() => {
			setCheckAndHandleTimeout();

			return () => {
				if (checkTimeoutRef.current) {
					clearTimeout(checkTimeoutRef.current);
				}
			};
		}, []);

		return (
			<MenuList
				{...props}
				innerRef={composeRefs<HTMLElement>(innerRef, menuListRef)}
			/>
		);
	}

	return WrappedMenuList;
}
import type { ReactElement, Ref } from "react";
import type {
	GroupBase,
	SelectInstance,
	Props as SelectProps,
} from "react-select";
import type {
	AsyncPaginateProps,
	UseAsyncPaginateResult,
	WithAsyncPaginateType,
} from "./types";
import { useAsyncPaginate } from "./useAsyncPaginate";
import { useComponents } from "./useComponents";

const defaultCacheUniqs: unknown[] = [];
const defaultComponents = {};

type SelectComponentType = <
	Option = unknown,
	IsMulti extends boolean = boolean,
	Group extends GroupBase<Option> = GroupBase<Option>,
>(
	props: SelectProps<Option, IsMulti, Group> & {
		ref?: Ref<SelectInstance<Option, IsMulti, Group>>;
	},
) => ReactElement;

export function withAsyncPaginate(
	SelectComponent: SelectComponentType,
): WithAsyncPaginateType {
	function WithAsyncPaginate<
		OptionType,
		Group extends GroupBase<OptionType>,
		Additional,
		IsMulti extends boolean = false,
	>(
		props: AsyncPaginateProps<OptionType, Group, Additional, IsMulti>,
	): ReactElement {
		const {
			components = defaultComponents,
			selectRef = undefined,
			isLoading: isLoadingProp,
			cacheUniqs = defaultCacheUniqs,
			...rest
		} = props;

		const asyncPaginateProps: UseAsyncPaginateResult<OptionType, Group> =
			useAsyncPaginate(rest, cacheUniqs);

		const processedComponents = useComponents<OptionType, Group, IsMulti>(
			components,
		);

		const isLoading =
			typeof isLoadingProp === "boolean"
				? isLoadingProp
				: asyncPaginateProps.isLoading;

		return (
			<SelectComponent
				{...props}
				{...asyncPaginateProps}
				isLoading={isLoading}
				components={processedComponents}
				ref={selectRef}
			/>
		);
	}

	return WithAsyncPaginate;
}
import type { GroupBase, OptionsOrGroups } from "react-select";

export const checkGroup = (group: unknown): group is GroupBase<unknown> => {
	if (!group) {
		return false;
	}

	const { label, options } = group as {
		label?: unknown;
		options?: unknown;
	};

	if (typeof label !== "string" && typeof label !== "undefined") {
		return false;
	}

	if (!Array.isArray(options)) {
		return false;
	}

	return true;
};

export const reduceGroupedOptions = <
	OptionType,
	Group extends GroupBase<OptionType>,
>(
	prevOptions: OptionsOrGroups<OptionType, Group>,
	loadedOptions: OptionsOrGroups<OptionType, Group>,
): OptionsOrGroups<OptionType, Group> => {
	const res = prevOptions.slice();

	const mapLabelToIndex: Record<string, number> = {};
	let prevOptionsIndex = 0;
	const prevOptionsLength = prevOptions.length;

	for (const optionOrGroup of loadedOptions) {
		const group = checkGroup(optionOrGroup)
			? optionOrGroup
			: ({
					options: [optionOrGroup],
				} as unknown as Group);

		const { label = "" } = group;

		let groupIndex = mapLabelToIndex[label];
		if (typeof groupIndex !== "number") {
			for (
				;
				prevOptionsIndex < prevOptionsLength &&
				typeof mapLabelToIndex[label] !== "number";
				++prevOptionsIndex
			) {
				const prevGroup = prevOptions[prevOptionsIndex];

				if (checkGroup(prevGroup)) {
					mapLabelToIndex[prevGroup.label || ""] = prevOptionsIndex;
				}
			}

			groupIndex = mapLabelToIndex[label];
		}

		if (typeof groupIndex !== "number") {
			mapLabelToIndex[label] = res.length;
			res.push(group);
		} else {
			res[groupIndex] = {
				...res[groupIndex],
				options: [...(res[groupIndex] as Group).options, ...group.options],
			};
		}
	}

	return res;
};
